vec4 quaternionFromEuler(vec3 angle){
    float x = angle.x / 2.;
    float y = angle.y / 2.;
    float z = angle.z / 2.;
    float sx = sin(x);
    float cx = cos(x);
    float sy = sin(y);
    float cy = cos(y);
    float sz = sin(z);
    float cz = cos(z);
    vec4 quat = vec4(0);
    quat.x = sx * cy * cz + cx * sy * sz;
    quat.y = cx * sy * cz + sx * cy * sz;
    quat.z = cx * cy * sz - sx * sy * cz;
    quat.w = cx * cy * cz - sx * sy * sz;
    return quat;
}
mat4 matrixFromRT(vec4 q, vec3 p){
    float x2 = q.x + q.x;
    float y2 = q.y + q.y;
    float z2 = q.z + q.z;
    float xx = q.x * x2;
    float xy = q.x * y2;
    float xz = q.x * z2;
    float yy = q.y * y2;
    float yz = q.y * z2;
    float zz = q.z * z2;
    float wx = q.w * x2;
    float wy = q.w * y2;
    float wz = q.w * z2;
    return mat4(
        1. - (yy + zz), xy + wz, xz - wy, 0,
        xy - wz, 1. - (xx + zz), yz + wx, 0,
        xz + wy, yz - wx, 1. - (xx + yy), 0,
